#include <bits/stdc++.h>

using namespace std;

#define int long long

const int Size = 2000000 + 5;

int n, a[Size];

bool big(int p, int q, int c)
{
	return (a[p] >= c && a[q] >= c);
}

bool small(int p, int q, int c)
{
	return (a[p] < c && a[q] < c);
}

bool chk(int x)
{
	for(int i = n, j = n; i >= 2 && j <= 2 * n - 2; i --, j ++)
	{
		if(big(i, i - 1, x) || big(j, j + 1, x))
		{
			return 1;
		}
		if(small(j, j + 1, x) || small(i, i - 1, x))
		{
			return 0;
		}
	}
	return small(1, 2 * n - 2, x) ;
}

signed main()
{
    //freopen("pyramid.in","r",stdin);
    //freopen("pyramid.out","w",stdout);
    cin >> n;
	for(int i = 1; i <= n * 2 - 1; i ++)
	{
		cin >> a[i];
	}
	int l = 0, r = n * 2 - 1 + 1;
	while(l + 1 < r)
	{
		int mid = (l + r) >> 1;
		if(chk(mid))
		{
			l = mid;
		}
		else
		{
			r = mid;
		}
	}
	cout << l << endl;
    return 0;
}

