using namespace std;

const int N = 3e5 + 10;

int n, h, l, r, res;
int a[N], b[N], c[N];

inline int check(int x) {
	for (int i = 1; i <= n; ++i)	c[i] = (a[i] >= x);
	int L = 1, R = n, mid = (L + R) >> 1, ok = 1;
	for (int i = 1; i < n - 1; i += 2) 
		if (!(a[i] == 0 && a[i + 1] == 1))	{ ok = 0; break; }
	if (ok && !a[n])	return 0;
	ok = 1;
	for (int i = 1; i < n - 1; i += 2)
		if (!(a[i] == 1 && a[i + 1] == 0))	{ ok = 0; break; }
	if (ok && a[n])		return 1;
	for (int i = 1; i <= h; ++i) {
		if (c[mid - 1] + c[mid] == 0 || c[mid] + c[mid + 1] == 0)	return 0;
		if (c[mid - 1] + c[mid] == 2 || c[mid] + c[mid + 1] == 2)	return 1;
		if (!c[mid] && (c[mid - 1] + c[mid + 1] + c[mid + 2] == 3 || c[mid - 2] + c[mid - 1] + c[mid + 1] == 3))	return 1;
		if (c[mid] && (c[mid - 1] + c[mid + 1] + c[mid + 2] == 0 || c[mid - 2] + c[mid - 1] + c[mid + 1] == 0))	return 0;
		L--, R--;
		for (int j = L; j <= R; ++j)	c[j] = ((c[j - 1] + c[j] + c[j + 1]) >= 2);
	}
	return c[mid];
}

int main() {

	scanf("%d", &n);
	h = n - 1, n = 2 * n - 1;
	for (int i = 1; i <= n; ++i)	scanf("%d", a + i);
	for (int i = 1; i <= n; ++i)	b[i] = a[i];

	sort(b + 1, b + n + 1);
	l = 1, r = n;
	while (l <= r) {
		int mid = l + r >> 1;
		if (check(b[mid]))	l = mid + 1, res = mid;
		else r = mid - 1;
	}

	printf("%d\n", b[res]);

	return 0;
}

