inline bool pd(int x)
{
	for(int i=0;i<n-1;i++)
	{
		if(check(a[n+i],a[n+i+1],x)||check(a[n-i],a[n-i-1],x))
		{
			return 1;
		}
		if(checkb(a[n+1],a[n+i+1],x)||checkb(a[n-i],a[n-i-1],x)) 
		{
			return 0;
		}
	}
	return check(a[1],a[1],x);
}
