    void Input()
    {
        scanf("%d",&n);
        for(int i=1;i<(n<<1);++i)
        {
            scanf("%d",&a[i]);
        }
    }

    int greater2(int x,int a,int b)
    {
        return (a>x) and (b>x);
    }
    int less2(int x,int a,int b)
    {
        return (a<=x) and (b<=x);
    }
    bool check(int Mid)
    {
        for(int i=0;i<n-1;++i)
        {
            if (greater2(Mid,a[n+i],a[n+i+1]) or greater2(Mid,a[n-i],a[n-i-1])) return 0;
            if (less2(Mid,a[n+i],a[n+i+1]) or less2(Mid,a[n-i],a[n-i-1])) return 1;
        }
        return (a[1]<=Mid);
    }
    void Solve()
    {
        int Low=1,High=(n<<1)-1;
        while(Low<High)
        {
            int Mid=(Low+High)>>1;
            if (check(Mid))
            {
                High=Mid;
            }
            else
            {
                Low=Mid+1;
            }
        }
        printf("%d\n",High);
    }
