    for(i = 1;i <= 2 * N - 1; i++){
        scanf("%d", &num[i]);
    }
    int ans = 0;
    while(l <= r){
        int mid = (l + r) >> 1;
        for(i = 1;i <= 2 * N - 1; i++){
            tmp[i] = (num[i] >= mid?1 : 0);
        }
        bool flag = false, ok = false;
        for(i = 1;i < N; i++){
            if(tmp[N-i] == tmp[N-i+1]){
                if(tmp[N-i] == 1){
                    flag = ok = true;
                    break;
                }
                else {
                    ok = true;
                    break;
                }
            }
            if(tmp[N+i] == tmp[N+i-1]){
                if(tmp[N+i] == 1){
                    ok = flag = true;
                    break;
                }
                else {
                    ok = true;
                    break;
                }
            }
        }
        if(ok == false && tmp[N] == 1 && N & 1)flag = true;
        if(flag){
            ans = max(ans, mid);
            l = mid + 1;
        }
        else r = mid - 1;
    }
