int N;
int A[202020];
int B[202020];

int ok(int cur) {
	if(cur>2*N-1) return 0;

	int i;
	FOR(i,2*N-1) B[i]=A[i]>=cur;
	if(B[N-1]==B[N-2] || B[N-1]==B[N]) return B[N-1];
	int L=N-1,R=N-1;
	while(L>0 && B[L]!=B[L-1]) L--;
	while(R<2*N-1 && B[R]!=B[R+1]) R++;

	if(L==0 && R==2*N-1) return B[L];
	if((N-1)-L<=R-(N-1)) return B[L];
	return B[R];
}


