int Small (int x, int y, int v) {return a[x] < v && a[y] < v;}
int Great (int x, int y, int v) {return a[x] >= v && a[y] >= v;}

bool exam (int k) {
	if (Small(n, n - 1, k) || Small(n, n + 1, k)) return 0;
	if (Great(n, n - 1, k) || Great(n, n + 1, k)) return 1;
	for (int i = 1; i < n - 1; ++i) {
		if (Small(n - i, n - i - 1, k) || Small(n + i, n + i - 1, k)) return 0;
		if (Great(n - i, n - i - 1, k) || Great(n + i, n + i + 1, k)) return 1;
	}
	return Great(1, 1, k);
}

