int N;
int bottom[200001];

bool top(int x){
    for(int i=1;i<N;i++){
        if((bottom[N-1+i]>=x)==(bottom[N-1+i-1]>=x)) return bottom[N-1+i]>=x;
        if((bottom[N-1-i]>=x)==(bottom[N-1-i+1]>=x)) return bottom[N-1-i]>=x;
    }
    return (bottom[0]>=x);
}

