        if(1 <= i && i <= 2 * n - 1
        && 1 <= j && j <= 2 * n - 1
        && 1 <= p && p <= 2 * n - 1
        && 1 <= q && q <= 2 * n - 1) {
            if(x(i, j) || x(p, q)) return 1;
            if(y(i, j) || y(p, q)) return 0;
        }
    } 
    return x(1, 1);
}

