int binsearch() {
	int l=0,r=m;
	while(l+1<r) {
		int mid=l+(r-l)/2;
		if(judge(mid)) l=mid;
		else r=mid;
	}
	if(judge(l+1)) return l+1;
	if(!judge(l)) return l-1;
	else return l;
}

