#include <stdio.h>

int N;
int input[200000];

int last(int K)
{
	int left = input[N - 2] >= K ? 1 : 0;
	int center = input[N - 1] >= K ? 1 : 0;
	int right = input[N] >= K ? 1 : 0;

	if (center == left || center == right) return center;

	int before = left;
	int left_time = -1, left_num;
	int right_time = -1, right_num;

	for (int i = N - 3; i >= 0; i--)
	{
		int cur = input[i] >= K ? 1 : 0;

		if (before == cur)
		{
			left_time = N - 1 - (i + 1);
			left_num = cur;
			break;
		}

		before = cur;
	}

	before = right;

	for (int i = N + 1; i < 2 * N - 1; i++)
	{
		int cur = input[i] >= K ? 1 : 0;

		if (before == cur)
		{
			right_time = i - 1 - (N - 1);
			right_num = cur;
			break;
		}

		before = cur;
	}

	if (left_time == -1) return right_num;
	if (right_time == -1) return left_num;

	if (left_num == right_num) return left_num;

	if (left_time < right_time) return left_num;
	else return right_num;
}

int main()
{
	scanf("%d", &N);

	for (int i = 0; i < 2*N-1; i++) scanf("%d", &input[i]);

	int low = 1, high = 2 * N - 1;

	while (low <= high)
	{
		if (high - low > 1)
		{
			int mid = (low + high) / 2;

			if (last(mid) == 1) low = mid;
			else high = mid - 1;
		}
		else
		{
			if (high == low) break;
			else
			{
				if (last(high) == 1) low = high;
				break;
			}
		}
	}

	printf("%d\n", low);

	return 0;
}

