using namespace std;

int N;
int a[200000];
int t[200000];

bool f(int s) {
  for (int i=0; i<2*N-1; i++) t[i] = a[i] >= s ? 1 : 0;

  int g = N-1;
  if (t[g] == t[g-1] || t[g] == t[g+1]) return t[g];
  int l = t[g-1];
  int r = t[g+1];
  for (int i=2; i<N; i++) {
    if (t[g-i] == l) {
      return i < N ? l : (t[g] + N + 1) % 2;
    }
    if (t[g+i] == r) {
      return i < N ? r : (t[g] + N + 1) % 2;
    }
    l = t[g-i];
    r = t[g+i];
  }
  return (t[g] + N + 1) % 2;
}

int main() {
  cin >> N;
  for (int i=0; i<2*N-1; i++) cin >> a[i];
  if (N == 2) {
    cout << "2\n";
  }
  else {
    int lo = 1, hi = 2*N-1;
    int mid;
    while (lo != hi) {
      mid = (lo + hi) / 2;
      if (f(mid)) {
        if (f(mid + 1)) lo = mid + 1;
        else {
          lo = mid;
          break;
        }
      }
      else hi = mid - 1;
    }
    cout << lo << "\n";
  }
  return 0;
}

