int solve(int lim)
{
    for(int i=1;i<=m;i++) 
		b[i]=(a[i]>=lim);
    num=0;
    int i=1;
    while(i<m)
    {
        if(b[i]!=b[i+1]) 
		{
			i++;
			continue;
		}
        A[++num].l=i;
		A[num].c=b[i];
        while(i<m && b[i+1]==b[i]) 
			i++;
        A[num].r=i;
    }
    if(num==0) 
		return b[1];
    A[0].r=0;
    for(int i=1;i<=num;i++)
    {
        int l=A[i-1].r+1;
		int r=A[i].l-1;
        if(l>r) 
			continue;
        int mid=(l+r)/2;
        A[i-1].r=mid;
		A[i].l=mid+1;
    }
    int l=A[num].r+1;
	int r=m,mid;
    if(l<=r) 
	{
		mid=(l+r)/2;
		A[num].r=mid;
	}
    for(int i=1;i<=num;i++) 	
		if(A[i].l<=n && A[i].r>=n) 
			return A[i].c;
}
