bool median(int val)
{
    for(int i = 1; i <= 2 * N - 1; i++) b[i] = (v[i] <= val);
    int cnt = 0;
    for(int i = 1; i < 2 * N - 1; i++)  if(b[i] == b[i + 1])    cnt++;
    if(!cnt)    return v[1];

    for(int l = 0; l < N; l++)
    {
        if(b[N - l] == b[N - l - 1])    return b[N - l];
        if(b[N + l] == b[N + l + 1])    return b[N + l];
    }

    return false;
}

