bool is_eq_greater(int val)
{
    vector<int> v(2 * N - 1);
    REP(i, 2 * N - 1) v[i] = (a[i] >= val);
    {
        bool flg = true;
        REP(i, N) if (!v[i * 2]) flg = false;
        if (flg) return true;
    }
    if (!v[N - 1] and !v[N]) return false;
    if (!v[N - 1] and !v[N - 2]) return false;
    int r2 = -1, l2 = -1, r1 = N - 1, l1 = N - 1;
    REP(k, N - 1)
    {
        if (v[N - 1 + k] + v[N - 1 + k + 1] == 0) break;
        r1 = N - 1 + k;

        if (v[N - 1 + k] + v[N - 1 + k + 1] == 2) { r2 = N - 1 + k + 1; break; }
    }
    REP(k, N - 1)
    {
        if (v[N - 1 - k] + v[N - 1 - k - 1] == 0) break;
        l1 = N - 1 - k;
        if (v[N - 1 - k] + v[N - 1 - k - 1] == 2) { l2 = N - 1 - k - 1; break; }
    }
    if (l2 >= 0 and r2 >= 0) return true;
    if (l2 < 0 and r2 >= 0 and (l1 + r1) / 2 <= N - 1) return true;
    if (r2 < 0 and l2 >= 0 and (l1 + r1) / 2 >= N - 1) return true;
    return false;
}


