		tmp[i] = !(x >= a[i]);
	}
	int l = n-2;
	for(;l >= 0;l--){
		if(tmp[l] == tmp[l+1])break;
	}
	l = max(l, 0);
	int r = n;
	for(;r < 2*n-1;r++){
		if(tmp[r] == tmp[r-1])break;
	}
	r = min(r, 2*n-2);
	if(abs(n-1-l) < abs(n-1-r))return tmp[l];
	else return tmp[r];	
}

