  vector<int> ord(n);
  iota(begin(ord), end(ord), 0);
  sort(begin(ord), end(ord), [&](int x, int y) {
    return a[x] < a[y];
  });
  for(int id = 0; id < n; id++) {
    int i = ord[id];
    int sg = sign(c - i);
    int d = abs(c - i);
    int aok = 0;
    if(d == 0) {
      dump(i, 1);
      if(is(c - 2, c - 1) && low[c - 1] != low[c + 1]) aok = 1;
      if(is(c + 1, c + 2) && low[c - 1] != low[c + 1]) aok = 1;
    } else {
      dump(i, 2);
      if(is(i + sg * 2 * d, i + sg) && (
        i > 0 && i < n - 1 ?
        low[i + 1] != low[i - 1] :
        1
        )) aok = 1;
      if(i > 1 && i < n - 2 && is(i + sg, i + sg * d * 2) && low[i - 1] == low[i + 1]) {
        int t, k;
        int ok, ng;
        ok = 0;
        ng = min(i, n - 1 - i);
        while(ng - ok > 1) {
          int mid = (ng + ok) >> 1;
          if(is(i + sg, i + sg * d * 2 + sg * mid)) ok = mid;
          else ng = mid;
        }
        t = ok;
        ok = 0;
        ng = min(i, n - 1 - i);
        while(ng - ok > 1) {
          int mid = (ng + ok) >> 1;
          if(is(i - sg, i - sg * mid)) ok = mid;
          else ng = mid;
        }
        k = ok;
        if(k >= 2) {
          if((t-1)/2 <= k || (k-1)/2 <= t) aok = 1;
        }
      }
    }
    if(i % 2 == 0 && i > 0 && i < n - 1 && is(0, i - 1) && is(i + 1, n - 1) && low[i - 1] == low[i + 1]) aok = 1;
    if(aok) return (cout << id + 1 << endl, 0);
