{
    if((a[n-1]<=x&&a[n]<=x)||(a[n]<=x&&a[n+1]<=x))return 1;
    if((a[n-1]> x&&a[n]> x)||(a[n]> x&&a[n+1]> x))return 0;
    for(int i=1;i<n-1;i++){
        if((a[n+i]<=x&&a[n+i+1]<=x)||(a[n-i]<=x&&a[n-i-1]<=x))return 1;
        if((a[n+i]> x&&a[n+i+1]> x)||(a[n-i]> x&&a[n-i-1]> x))return 0;
    }
    return a[1]<=x;
}

