int kt(vector<int> &a)
{
    int n = a.size();
    int mid = a.size()/2;
    {
        int a1 = a[mid], a2 = a[mid-1], a3 = a[mid+1];
        if(a1==a2||a2==a3)
            return a2;
    }
    int l = mid, r = mid;
    while(l>=0)
    {
        if(a[l]==(1-a[l-1]))
        l--;
        else
        break;
    }
    while(r<n)
    {
        if(a[r]==(1-a[r+1]))
        r++;
        else
        break;
    }
    int x = r-mid;
    int y = mid-l;
    if(x>y) return a[l];
    return a[r];
}
