	bool ans = vec[0];
	for(int i = 0; i < n - 1; i++)
	{
		if(vec[n-1+i]==vec[n+i]) return vec[n-1+i];
		if(vec[n-1-i]==vec[n-2-i]) return vec[n-1-i];
	}
	return ans;
}

