	for(int i = 0; i < 2*n-1; i++)
	{
		cin>>p[i];
	}
	int lo = 1; int hi = 2*n-1;
	while(lo <= hi)
	{
		int mid = (lo+hi)>>1;
		vector<bool> vec(2*n-1);
		for(int i = 0; i < 2*n - 1; i++)
		{
			vec[i] = (p[i]>=mid);
		}
		if(solve(vec))
		{
			lo = mid + 1;
			ans = mid;
		}
		else
		{
			hi = mid - 1;
		}
	}
	cout << ans << '\n';
}

