bool check(int k)
{
	for(int i=0;i<n-1;i++)
	{
		int c1=0,c2=0;
		if(a[n-i]>=k&&a[n-i-1]>=k) c1++;
		if(a[n+i]>=k&&a[n+i+1]>=k) c1++;
		if(a[n-i]<k&&a[n-i-1]<k) c2++;
		if(a[n+i]<k&&a[n+i+1]<k) c2++;
		if(c1) return 1;
		if(c2) return 0;
	}
	return a[1]>=k;
}
