// ans >= k ?
bool solve(int k) {
  int cnt = 1;
  int c = m - 1;
  vector<int> b(n);
  for(int i = 1; i < n; i++) {
    if((a[i] >= k) == (a[i - 1] >= k)) {
      cnt++;
    } else {
      b.emplace_back(cnt);
      cnt = 1;
    }
  }
  b.emplace_back(cnt);
  int now = 0;
  cnt = 0;
  for(int i = 0; i < b.size(); i++) {
    if(b[i] != 1) {
      int l = now - cnt, r = now - 1;
      if(l <= c && c <= r) {
        int f = a[now] >= k;
        int t = a[now - cnt - 1] >= k;
        if(c - r > r - c) return f;
        return t;
      }
      l = now, r = now + b[i] - 1;
      if(l <= c && c <= r) {
        return a[now] >= k;
      }
      cnt = 0;
    } else cnt++;
    now += b[i];
  }
  return a[0] >= k;
}
