            return a[pos] ^ 1;
        else
            return a[pos];
    }

    if (a[pos + 1] == a[pos] || a[pos - 1] == a[pos])
        return a[pos];

    int l = 1;
    while (a[pos - l] != a[pos - l + 1] && a[pos + l] != a[pos + l - 1])
