using namespace::std;

void solve(){
	int N;
	cin >> N;
	int size = 2*N-1;
	vector<int> A(size);
	for(int i=0;i<size;++i){
		cin >> A[i];//[1,2N-1]
	}
	/////
	int OK = 1;
	int NG = 2*N;
	int mid;
	vector<int> v(size);
	const int cen = N-1;
	while(NG-OK>1){
		mid = (OK+NG)/2;
		for(int i=0;i<size;++i){
			v[i] = (A[i] >= mid);
		}

		if( v[cen] == v[cen-1] || v[cen] == v[cen+1] ){
			if( v[cen] ){
				OK = mid;
			}else{
				NG = mid;
			}
			continue;
		}

		int L = N;
		int R = N;
		int now = v[cen];
		for(int i=cen-1;i>=0;--i){
			if( now != v[i] ){
				L = i;
				now = v[i];
			}else{
				++L;
				break;
			}
		}
		now = v[cen];
		for(int i=cen+1;i<2*N-1;++i){
			if( now != v[i] ){
				R = i;
				now = v[i];
			}else{
				--R;
				break;
			}
		}
		//[L,R]

		int LLval = -1;
		int RRval = -1;
		if( L != 0 ){
			LLval = v[L-1];
		}
		if( R != 2*N-2 ){
			RRval = v[R+1];
		}

		if( R-L+1 == 2*N-1 ){//
			int res;
			if(N&1){
				res = v[cen];
			}else{
				res = (v[cen]+1)%2;
			}
			if( res ){
				OK = mid;
			}else{
				NG = mid;
			}
			continue;
		}
		int Len = (R-L+1);
		if(Len&1){//=
			if(LLval){
				OK = mid;
			}else{
				NG = mid;
			}
			continue;
		}else{//
			int res;
			if( (cen-L) > (R-cen) ){
				//
				res = RRval;
			}else{
				//
				res = LLval;
			}
			if( res ){
				OK = mid;
			}else{
				NG = mid;
			}
			continue;
		}
	}
	cout << OK << endl;
}

