bool positive(vector<int> &value, int x){
    int n = (int)value.size();
    int m = n / 2;
    int a = m - 1, b = m + 1;

    while (b < n && (value[b] >= x) != (value[b-1]>=x))
        b++;
    while (a >= 0 && (value[a] >= x) != (value[a+1]>=x))
        a--;
    a++;
    b--;

    if ((value[b]>=x)==(value[a]>=x))
        return value[b]>=x;

    if (b - m < m - a)
        return value[b] >= x;
    else
        return value[a] >= x;
}

