const int N=2e5+10;

int n,A[N],now[N];

void Init()
{
	scanf("%d",&n);
	n=2*n-1;
	for (int i=1;i<=n;++i) scanf("%d",&A[i]);
}

int len[N],op[N];
bool Check(int x)
{
	for (int i=1;i<=n;++i) if (A[i]>=x) now[i]=1;else now[i]=0;
	int top=0,cnt=0;
	for (int i=1;i<=n;)
	{
		int pos=i;
		while (now[pos]==now[i] && i<=n) ++i;
		if (i-pos>1)
		{
			if (top && now[pos]==op[top]) len[top]+=i-pos+cnt;
			else 
			{
				if (top) len[top]+=cnt>>1;
				++top; len[top]=i-pos+(cnt>>1);
				op[top]=now[pos];
			}
			cnt=0;
		}else cnt++;
	}
	if (!top) return now[1];
	int l=1,r=top;
	while (l!=r)
	{
		int del=min(len[l],len[r]);
		len[l]-=del; len[r]-=del;
		if (!len[l]) ++l;
		if (!len[r]) ++r;
	}
	return op[l];
}

void Solve()
{
	int l=1,r=n;
	while (l<=r)
	{
		int mid=(l+r)>>1;
		if (Check(mid)) l=mid+1;
		else r=mid-1;
	}
	printf("%d\n",r);
}

int main()
{
	Init();
	Solve();	
	return 0;
}

