using namespace std;
int calc(vector<int>v)
{
	int m = v.size() / 2;
	int c1 = 0, c2 = 0;
	for (int i = m - 1; i >= 0; i--)
	{
		if (v[i] == v[i + 1])
		{
			c1 = i + 1;
			break;
		}
	}
	for (int i = m + 1; i < v.size(); i++)
	{
		if (v[i] == v[i - 1])
		{
			c2 = i - 1;
			break;
		}
	}
	if (v[c1] == v[c2])return v[c1];
	if (m - c1 < c2 - m)return v[c1];
	else return v[c2];
}
int main()
{
	int num;
	scanf("%d", &num);
	vector<int>v;
	for (int i = 0; i < num * 2 - 1; i++)
	{
		int z;
		scanf("%d", &z);
		v.push_back(z);
	}
	int beg = 0, end = num * 2 + 1;
	for (;;)
	{
		if (beg == end)break;
		int med = (beg + end + 1) / 2;
		vector<int>z;
		for (int i = 0; i < v.size(); i++)z.push_back(v[i] >= med);
		if (calc(z))beg = med;
		else end = med - 1;
	}
	printf("%d\n", beg);
}

