#define repeat(i, n) for (int i = 0; (i) < int(n); ++(i))
#define repeat_from(i, m, n) for (int i = (m); (i) < int(n); ++(i))
using ll = long long;
using namespace std;

int median(int a, int b, int c) {
    if (a > b) swap(a, b);
    return max(a, min(b, c));
}

template <typename UnaryPredicate>
ll binsearch(ll l, ll r, UnaryPredicate p) { // [l, r), p is monotone
    assert (l < r);
    -- l;
    while (r - l > 1) {
        ll m = (l + r) / 2;
        (p(m) ? r : l) = m;
    }
    return r; // = min { x in [l, r) | p(x) }, or r
}

int main() {
    // input
    int n; scanf("%d", &n);
    vector<int> a(2 * n - 1);
    repeat (i, 2 * n - 1) {
        scanf("%d", &a[i]);
    }
    // solve
    if ((n - 1) % 2 == 1) {
        vector<int> b(2 * n - 3);
        repeat (i, 2 * n - 3) {
            b[i] = median(a[i], a[i + 1], a[i + 2]);
        }
        a.swap(b);
        -- n;
    }
    int result = binsearch(1, 2 * n, [&](int x) {
        vector<bool> b(2 * n - 1);
        repeat (i, 2 * n - 1) {
            b[i] = a[i] > x;
        }
        for (int l = 1; l < 2 * n - 1;) {
            if (b[l - 1] == b[l]) {
                int cl = b[l];
                int r = l;
                while (r + 1 < 2 * n - 1 and b[r] != b[r + 1]) ++ r;
                int cr = r + 1 < 2 * n - 1 ? b[r] : cl;
                int m = l + (r - l + 1) / 2;
                repeat_from (i, l, m) b[i] = cl;
                repeat_from (i, m, r) b[i] = cr;
                l = r + 1;
            } else {
                ++ l;
            }
        }
        return not b[n - 1];
    });
    // output
    printf("%d\n", result);
    return 0;
}

