void solve() {
  int n; cin >> n;
  vi arr(2*n-1); cin >> arr;

  int l = 1, r = 2*n-1;

  while (r - l > 1) {
    int mid = (l + r) / 2;
    int x = n-1, y = n-1;
    while (x > 0 && (arr[x-1] < mid) ^ (arr[x] < mid)) --x;
    while (y < 2*(n-1) && (arr[y] < mid) ^ (arr[y+1] < mid)) ++y;

    if ((x + y) & 1) {
      (x + y < 2*(n-1)) ^ (arr[x] < mid) ? r = mid : l = mid;
    } else {
      arr[x] < mid ? r = mid : l = mid;
    }
  }

  cout << l << endl;
}


