bool Check(int Key){
	for(int i=0;i<N-1;i++){
		if( (A[N+i]>Key && A[N+i+1]>Key) || (A[N-i]>Key && A[N-i-1]>Key) ){
			return 0;
		}
		if( (A[N+i]<=Key && A[N+i+1]<=Key) || (A[N-i]<=Key && A[N-i-1]<=Key) ){
			return 1;
		}
	}
	return A[1]<=Key;
}

int BS(int L,int R){
	int Ans=0;
	while(L<=R){
		int Mid=(L+R)>>1;
		if(Check(Mid)){
			Ans=Mid;
			R=Mid-1;
		}else{
			L=Mid+1;
		}
	}
	return Ans;
}

