		if ((!b[n/2-i+1] && !b[n/2-i+2]) || (!b[n/2+i] && !b[n/2+i+1])) return 0;
		if ((b[n/2-i+1] && b[n/2-i+2]) || (b[n/2+i] && b[n/2+i+1])) return 1;
	}
	return b[1];
}

