void ins(int a, int b, int c) {
    if (a > b) return;
    if (a == b) c |= 2;
    cur.insert({a,b,c});
    if (c >= 2) alt.insert({a,b,c});
}

void del(array<int,3> a) {
    cur.erase(a);
    if (a[2] >= 2) alt.erase(a);
}

void init(int mid) {
    cur.clear(), alt.clear();
    vpi CUR;
    F0R(i,2*N-1) {
        int tmp = (A[i] <= mid);
        if (sz(CUR) == 0 || CUR.back().f != tmp) CUR.pb({tmp,0});
        CUR.back().s ++;
    }
    for (int l = 0, i = 0; i < sz(CUR); ) {
        if (CUR[i].s == 1) {
            int I = i; while (i < sz(CUR) && CUR[i].s == 1) i++;
            ins(l,l+i-I-1,CUR[I].f+2);
            l += i-I;
        } else {
            ins(l,l+CUR[i].s-1,CUR[i].f);
            l += CUR[i].s;
            i ++;
        }
    }
    //for (auto a: CUR) cout << a.f << " " << a.s << "\n";
    //prin(cur);
    //exit(0);
}

void remLeft() {
    auto a = *cur.begin(); del(a);
    if (a[2] >= 2) ins(a[0]+1,a[1],a[2]^1);
    else ins(a[0]+1,a[1],a[2]);
}

void remRight() {
    auto a = *prev(cur.end()); del(a);
    if (a[2] >= 2) ins(a[0],a[1]-1,a[2]);
    else ins(a[0],a[1]-1,a[2]);
}

int CO = 0;

void process() {
    // prin(cur);

    set<array<int,3>> ALT; swap(alt,ALT);
    for (auto a: ALT) {
        // cout << "HI " << a[0] << " " << a[1] << " " << a[2] << "\n";
        auto it = cur.find(a); 
        if (a[0] == a[1]) {
            if (it != cur.begin() && next(it) != cur.end()) {
                auto a = *prev(it), b = *next(it);
                cur.erase(prev(it)); cur.erase(next(it)); cur.erase(it);
                ins(a[0],b[1],a[2]);
            }
        } else {
            if (next(it) != cur.end()) {
                auto IT = next(it);
                auto A = *IT; cur.erase(IT);
                A[0] --, a[1] --;
                cur.insert(A);
            }
            if (it != cur.begin()) {
                auto IT = prev(it);
                auto A = *IT; cur.erase(IT);
                A[1] ++, a[0] ++; a[2] ^= 1;
                cur.insert(A);
            }
            cur.erase(it);
            ins(a[0],a[1],a[2]^1);
        }
    }

    // prin(cur);
    remLeft();
    remRight();

    // prin(cur);
    // for (auto a: alt) cout << "OOPS " << a[0] << " " << a[1] << " " << a[2] << "\n";
    // CO ++; if (CO == 3) exit(0);
}

