		cin >> a[i];
	}
	l = 1;
	r = N;
	while(l <= r) {
		m = (l + r) / 2;
		for(int i = 1; i <= N; ++i) {
			if(a[i] <= m) b[i] = 1;
			else b[i] = 0;
		}
		if(ShAM()) {
			fp = m;
			r = m - 1;
		} else {
			l = m + 1;
		}
	}
	cout << fp << endl;
	return 0;
}

