bool jugde(int x)
{
    for(int i=1;i<=n;i++)
    {
        if(a[i]>=x) b[i]=1;
        else        b[i]=0;
    }
    for(int l=n/2,r=n/2+2;l>=1&&r<=n;l--,r++)
    {
        if (b[l] ^ b[l+1] && b[r] ^ b[r-1]) continue;
        if(b[l]==b[l-1]) return b[l];
        if(b[r]==b[r+1]) return b[r];
    }
    return b[1];
}
