void solve(){
    int n;
    cin >> n;
    vector<int> a(2*n-1);
    rep(i,0,2*n-1) cin >> a[i];

    auto ok=[&](int x){
        vector<int> s(2*n-1);
        rep(i,0,2*n-1){
            if(a[i]>=x) s[i]=1;
        }
        if(s[n-2]==s[n-1] or s[n-1]==s[n]) return bool(s[n-1]);
        {
            bool f=true;
            rep(i,1,2*n-1){
                if(s[i]==s[i-1]){
                    f=false;
                    break;
                }
            }
            if(f) return bool((s[n-1]+n+1)%2);
        }

        vector<tuple<int,int,int>> w;
        rep(i,0,2*n-2){
            if(s[i]==s[i+1]){
                int j=i;
                while(s[j]==s[i] and j<2*n-1) ++j;
                --j;
                w.push_back(make_tuple(s[i],i,j));
                i=j;
            }
        }

        vector<tuple<int,int,int>> v;
        rep(i,0,w.size()){
            int j=i;
            while(get<0>(w[j])==get<0>(w[i]) and j<w.size()) ++j;
            --j;
            v.push_back(make_tuple(get<0>(w[i]),get<1>(w[i]),get<2>(w[j])));
            i=j;
        }
        if(v.size()==1) return bool(get<0>(v[0]));

        rep(i,0,v.size()-1){
            int l1=get<1>(v[i]),r1=get<2>(v[i]),l2=get<1>(v[i+1]),r2=get<2>(v[i+1]);
            int l=(r1+l2)/2,r=(r1+l2)/2+1;
            if(l1<=n-1 and n-1<=l) return bool(get<0>(v[i]));
            if(r<=n-1 and n-1<=r2) return bool(get<0>(v[i+1]));
        }
    };

    int lb=1,ub=2*n;
    while(ub-lb>1){
        int m=(lb+ub)/2;
        if(ok(m)) lb=m;
        else ub=m;
    }
    cout << lb << endl;
}

int main(){
    std::cin.tie(0);
    std::ios::sync_with_stdio(false);
    cout.setf(ios::fixed);
    cout.precision(10);
    solve();
