bool cal(int md){
    int mn[2]={inf,-inf};
    for(int i=0;i<n-1;i++){
        if((a[n+i]>=md)==(a[n+i+1]>=md))mn[a[n+i]>=md]=min(mn[a[n+i]>=md],i);
