        {
            c[i] = b[i], c[i + 1] = b[i + 1];
        }
    }
    for (int i = 0; i < N; i++)
    {
        if (c[i] == -1)
        {
            int j = i;
            while (c[j + 1] == -1) j++;
            if (i % 2 == j % 2)
            {
                for (int k = i; k <= j; k++) c[k] = c[i - 1];
            }
            else
            {
                int m = (i + j) / 2;
                for (int k = i; k <= m; k++) c[k] = c[i - 1];
                for (int k = m + 1; k <= j; k++) c[k] = c[j + 1];
            }
            i = j;
        }
    }
}

