// [l, r) from l to r
struct range {
  struct itr {
    int i;
    constexpr itr(int i_): i(i_) { }
    constexpr void operator ++ () { ++i; }
    constexpr int operator * () const { return i; }
    constexpr bool operator != (itr x) const { return i != x.i; }
  };
  const itr l, r;
  constexpr range(int l_, int r_): l(l_), r(std::max(l_, r_)) { }
  constexpr itr begin() const { return l; }
  constexpr itr end() const { return r; }
};

// [l, r) from r to l
struct revrange {
  struct itr {
    int i;
    constexpr itr(int i_): i(i_) { }
    constexpr void operator ++ () { --i; }
    constexpr int operator * () const { return i; }
    constexpr bool operator != (itr x) const { return i != x.i; }
  };
  const itr r, l;
  constexpr revrange(int l_, int r_): l(l_ - 1), r(std::max(l_, r_) - 1) { }
  constexpr itr begin() const { return r; }
  constexpr itr end() const { return l; }
};

int main() {
  int H;
  std::cin >> H;
  int N = 2 * H - 1;
  std::vector<int> A(N);
  for (int &x: A) {
    std::cin >> x;
  }
  int ok = 1, ng = N + 1;
  while (ng - ok > 1) {
    int md = (ok + ng) / 2;
    ([&] {
      std::vector<bool> base(N);
      for (int i: range(0, N)) {
        base[i] = (A[i] >= md);
      }
      int L = 0, R = N - 1;
      for (int i: range(0, H - 1)) {
        if (!base[i] && !base[i + 1]) {
          L = i + 2;
        }
      }
      for (int i: revrange(H, N)) {
        if (!base[i] && !base[i - 1]) {
          R = i - 2;
        }
      }
      while (L < N && !base[L]) {
        ++L;
      }
      while (R >= 0 && !base[R]) {
        --R;
      }
      if (L >= R) {
        return false;
      }
      if (L == 0 && R == N - 1) {
        return true;
      }
      std::vector<int> pos;
      {
        int i = L;
        while (i < R) {
          if (base[i] && base[i + 1]) {
            int k = i;
            while (k <= R && base[k]) {
              if (k == H - 1) {
                return true;
              }
              ++k;
            }
            pos.push_back(i);
            pos.push_back(k - 1);
            i = k;
          }
          else {
            ++i;
          }
        }
      }
      if (pos.empty()) {
        return false;
      }
      if ((L + pos.front()) / 2 <= H - 1 && H - 1 <= pos.front()) {
        return true;
      }
      if (pos.back() <= H - 1 && H - 1 <= (pos.back() + R) / 2) {
        return true;
      }
      for (int i: range(0, pos.size() - 1)) {
        if (pos[i] <= H - 1 && H - 1 <= pos[i + 1]) {
          return true;
        }
      }
      return false;
    }() ? ok : ng) = md;
  }
  std::cout << ok << '\n';
  return 0;
}

