int N,M,A[L],B[L];
struct edge {int l,r,val;} line[L];

inline bool check(int x){
	for(R int i=1;i<=M;++i) B[i] = A[i]>=x;
	R int cnt=0;
	for(R int i=1,j;i<=M;++i){
		j=i;
		while(j<M && B[i]==B[j+1]) j++;
		if(i==j) continue;
		line[++cnt]=(edge){i,j,B[i]};
		i=j;
	}
	if(!cnt) return B[1];
	line[0].r=0;
	for(R int i=1;i<=cnt;++i){
		R int l=line[i-1].r+1,r=line[i].l-1;
		if(l>r) continue;
		R int mid=l+r>>1;
		line[i-1].r=mid,line[i].l=mid+1;
	}
	R int l=line[cnt].r+1,r=M,mid=l+r>>1;
	if(l<=r) line[cnt].r=mid;
	for(R int i=1;i<=cnt;++i)
		if(line[i].l<=N && N<=line[i].r) return line[i].val;
}

