bool judge(int k)
{
	for(int i=0;i<n-1;i++)
	{
		if((a[n+i]>k&&a[n+i+1]>k)||(a[n-i]>k&&a[n-i-1]>k))
			return false;
		if((a[n+i]<=k&&a[n+i+1]<=k)||(a[n-i]<=k&&a[n-i-1]<=k))
			return true;
	}
	return a[1]<=k&&a[1]<=k;;
}



