int median(int i,int j,int k){
	if (i==j) return i;
	if (j==k) return j;
	if (k==i) return k;

	int l=MIN(i,j,k),r=MAX(i,j,k);
	if (l<i && i<r) return i;
	else if (l<j && j<r) return j;
	else return k;
}

