void work() {
    int n, x;
    scanf("%d%d", &n, &x);
    int t = n * 2 - 1;
    for (int i = 1; i <= t; ++i) {
        scanf("%d", &a[0][i]);
    }
    int now = 0;
    while (true) {
        memset(book, 0, sizeof book);
        for (int i = 2; i <= t - 1; ++i) {
            b[1] = a[now][i - 1];
            b[2] = a[now][i];
            b[3] = a[now][i + 1];
            sort(b + 1, b + 1 + 3);
            book[b[2]]++;
            a[!now][i - 1] = b[2];
        }
        int ans = 1, anstime = book[1];
        for (int i = 1; i <= t; ++i) {
            if (anstime < book[i]) {
                ans = i;
                anstime = book[i];
            }
        }
        bool flag = true;
        for (int i = 1; i <= t; ++i) {
            if (book[i] == anstime && i != ans) {
                flag = false;
                break;
            }
        }
        if (flag) {
            printf("%d\n", ans);
            return;
        }
        t -= 2;
        now = !now;
    }
}

