                binSearchHelper(bl, lo, hi, mid);
            }
            else if (r - n < n - l) { // r is closer
                binSearchHelper(br, lo, hi, mid);
            }
            else { // l is closer
                binSearchHelper(bl, lo, hi, mid);
            }
