int main()
{
    scanf("%d",&n);
    for(int i=1;i<=2*n-1;i++){
    scanf("%d",&a[i]);  
    }
    l=0;r=1e8+7;
    while(l<=r)
    {
        mid=(l+r)/2;
        int f=1e8+7,q=1e8+7;
        for(int i=1;i<=2*n-1;i++)
        {   
        if(a[i]>=mid)b[i]=1;
        else b[i]=0;
        }
        for(int i=n+1,j=n-1;i<=n*2-1&&j>=1;i++,j--)
        {
            if(b[j]==0&&b[j+1]==0)f=min(f,n-j);
