using namespace std;
int a[200010];
int middle(int x,int y,int z){
	vector<int> t;
	t.push_back(x);
	t.push_back(y);
	t.push_back(z);
	sort(t.begin(),t.end());
	return t[1];
}
int main(){
	int n;
	cin>>n;
	vector<int> v;
	for(int i=0;i<2*n-1;i++){
		cin>>a[i];
		v.push_back(a[i]); 
	}
	for(int i=0;i<n;i++){
		vector<int> vv(2*n-1);
		for(int j=n-1;j>i;j--){
			vv[j]=middle(v[j-1],v[j],v[j+1]);
			if(vv[j]==vv[j+1])break;
		}
		for(int j=n-1;j<2*n-1;j++){
			vv[j]=(middle(v[j-1],v[j],v[j+1]));
			if(vv[j]==vv[j-1])break;
		}
		v.swap(vv);
	}
	cout<<v[n-1]<<endl;
	return 0;
}

