signed main(){
  cin.tie(0);
  ios_base::sync_with_stdio(0);
  cout << fixed << setprecision(12);

  Int n;
  cin>>n;
  n = 2 * n - 1;
  vector<Int> A(n);
  cin>>A;

  auto getDis = [&](const vector<Int> &X, Int num){
    Int res = INF;
    Int n = X.size();
    for(Int i=0;i<n-1;i++) {
      if(X[i] == num && X[i+1] == num) Min(res, abs(n/2 - i));
      if(X[i] == num && X[i+1] == num) Min(res, abs(n/2 - (i+1)));
    }
    return res;
  };

  auto check=[&](Int x){
    vector<Int> X(n);
    for(Int i=0;i<n;i++) X[i] = A[i] >= x;

    {
      Int flag = 1;
      for(Int i=0;i<n-1;i++) flag &= X[i] != X[i+1];
      if(flag) return X[0];
    }

    Int one = getDis(X, 1);
    Int zero = getDis(X, 0);
    assert(one != zero);
    return (Int)(one < zero);
  };

  Int L = 1, R = n;
  while(L+1 < R){
    Int M = (L+R)/2;
    check(M)? L = M: R = M;
  }

  Int ans = L;
  cout<<ans<<endl;

  return 0;
}

