    return a[i]>k&&a[j]>k;
} 
bool check(int a){
    for(int i=0;i<n-1;i++){
        if(y(n+i,n+i+1,a)||y(n-i,n-i-1,a)) return 0;
        if(x(n+i,n+i+1,a)||x(n-i,n-i-1,a)) return 1;
    }
    return x(1,1,a);
}
