int a[N], x[N];
int n;

bool check(int val) {
    for(int i = 1; i < 2 * n; ++i) {
        x[i] = (a[i] >= val);
    }

    if (x[n] == x[n + 1] || x[n] == x[n - 1]) return x[n];

    int r = n + 1;
    while (r < 2 * n && x[r] != x[r - 1]) ++r;
    int l = n - 1;
    while (l > 0 && x[l] != x[l + 1]) --l;

    if (l == 0 && r == 2 * n) return x[n] ^ 1;

    if (l == 0) return x[r];
    if (r == 2 * n) return x[l];

    if (x[l] == x[r]) return x[l];

    if (n - l < r - n) return x[l];
    else return x[r];
}

void solve(){
    cin >> n;
    for(int i = 1; i < 2 * n; ++i) scanf("%d", a + i);


//    for(int i = 2; i < 2 * n - 1; ++i) cout << check(i) << " ";
//    puts("");

    int lo = 2, hi = 2 * n - 2, res = 1;
    while (lo <= hi) {
        int g = (lo + hi) >> 1;
        if (check(g)) {
            res = g;
            lo = g + 1;
        } else hi = g - 1;
    }

    cout << res;


}

