const int N = 100010;
int a[2][N * 2 + 10];

inline int median(int a, int b, int c) {
    if (a >= b) {
        if (b >= c) return b;
        if (c >= a) return a;
        return c;
    }
    else {
        if (a >= c) return a;
        if (c >= b) return b;
        return c;
    }
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        for (int i = 1; i < 2 * n; ++i) scanf("%d", &a[0][i]);
        int now = 0;
        bool yes = 0;
        int cnt = 0;
        while (!yes && a[now][n-1] != a[now][n] && a[now][n+1] != a[now][n]) {
            now ^= 1;
            cnt++;
            yes = 1;
            for (int i = cnt + 1; i < 2 * n - cnt; ++i) {
             //   if (a[now^1][i] <= a[now^1][i-1]) yes = 0;
                a[now][i] = median(a[now^1][i-1], a[now^1][i], a[now^1][i+1]);
            }
            for (int i = cnt + 2; i < 2 * n - cnt; ++i)
                if (a[now][i] < a[now][i-1]) {
                    yes = 0;
                    break;
                }
            //if (a[now^1][2 * n - now] <= a[now^1][2*n-now-1]) yes = 0;
        }
        //printf("%d\n", cnt);
        printf("%d\n", a[now][n]);
    }
    return 0;
}

